% radiometer_constraints.m
% Generates Figure 4 using data generated by Tb_data.mat for the
% publication 
% 
% Wolfenbarger, N. S., Broome, A. L., Schroeder, D. M., Ermakov, A. I.,
% Bolton, S. J., and Blankenship, D. D. (2025). Passive Microwave
% Radiometry and Active Radar Sounding as Complementary Tools for
% Geophysical Investigations of Icy Ocean Worlds.

clear all; close all; clc

%% Defaults
fontsize = 8;
linewidth = 2;
interpreter = 'latex';

%% Constants
Ts = 100;

%% Freezing Point Depression
% Temperature Profile
p = 4;
subplot(3,3,p)
N = 1e4;
Tm = 250;
z = linspace(0,1,N)';
T = Ts*(Tm/Ts).^(z);

plot(T,z,'k','LineWidth',linewidth)
ax(p) = gca;
ax(p).YDir = 'reverse';
ax(p).FontSize = fontsize;
ax(p).TickLabelInterpreter = interpreter;
ax(p).XTick = [Ts Tm];
ax(p).XTickLabel = {'$100$';'$f(D)-\Delta T_f$'};
ax(p).XLabel.String = 'Temperature, $T$ (K)';
ax(p).XLabel.FontSize = fontsize;
ax(p).XLabel.Interpreter = interpreter;
ax(p).YLabel.String = 'Fractional Depth, $z/D$';
ax(p).YLabel.FontSize = fontsize;
ax(p).YLabel.Interpreter = interpreter;
ax(p).XLim = [Ts-50 270+50];
ax(p).YLim = [0 1];

t1 = text(Ts,0,'$T_s$');
t1.HorizontalAlignment='center';
t1.VerticalAlignment ='bottom';
t1.FontSize = fontsize;
t1.Interpreter = interpreter;

t2 = text(Tm,0,'$T_m$');
t2.HorizontalAlignment='center';
t2.VerticalAlignment ='bottom';
t2.FontSize = fontsize;
t2.Interpreter = interpreter;

% Brightness Temperature Difference Contour
p = 5;
subplot(3,3,p)
load('Tb_deltaTm.mat')
delta_Tb = squeeze(Tb(1,:,:)-Tb(2,:,:));
x = repmat(D'/1e3,1,length(deltaTm));
y = repmat(deltaTm,length(D),1);


[C,h] = contour(y.',x.',delta_Tb.',[10:5:40],'LineWidth',linewidth,'LineColor',[0 112 192]/255);
clabel(C,h,'Interpreter','latex','FontSize',fontsize,'Color',[0 112 192]/255,...
    'BackgroundColor','w','LabelSpacing', 700)
hold on

ax(p) = gca;
ax(p).FontSize = fontsize;
ax(p).TickLabelInterpreter = interpreter;
ax(p).XLabel.String = 'Freezing Point Depression, $\Delta T_f$ (K)';
ax(p).XLabel.FontSize = fontsize;
ax(p).XLabel.Interpreter = interpreter;
ax(p).YLim = [0 100];
ax(p).YTick = 0:20:100;
ax(p).YLabel.String = 'Ice Shell Thickness, $D$ (km)';
ax(p).YLabel.FontSize = fontsize;
ax(p).YLabel.Interpreter = interpreter;
ax(p).Title.String = '$\Delta \mathcal{T}_{ice,0.6/1.2}=\mathcal{T}_{ice,0.6}-\mathcal{T}_{ice,1.2}$ (K)';
ax(p).Title.FontSize = fontsize;
ax(p).Title.Interpreter = interpreter;
box on


% Compatible Ice Shell Thickness
p = 6;
subplot(3,3,p)
deltaTb_vec = [0:0.1:50];

load('Tb_deltaTm.mat')
deltaTb_deltaTm = squeeze(Tb(1,:,:)-Tb(2,:,:));
C = contourc(deltaTm,D'/1e3,deltaTb_deltaTm,deltaTb_vec);
S = contourdata(C);

for n = 1:length(S)
    plot(S(n).level*ones(S(n).numel,1),S(n).ydata,'Color',[1 1 1]*0.8,...
        'LineWidth',linewidth)
    hold on
end

load('Tb.mat')
deltaTb = squeeze(Tb(1,:,:)-Tb(2,:,:));
[deltaTb_max,ind] = max(deltaTb);
D_vec1 = interp1(deltaTb(1:ind),D(1:ind)/1e3,deltaTb_vec);
D_vec2 = interp1(deltaTb(ind:end),D(ind:end)/1e3,deltaTb_vec);
plot(deltaTb_vec,D_vec1,'Color',[0 112 192]/255,'LineWidth',linewidth)
plot(deltaTb_vec,D_vec2,'Color',[0 112 192]/255,'LineWidth',linewidth)

ax(p) = gca;
ax(p).FontSize = fontsize;
ax(p).TickLabelInterpreter = interpreter;
ax(p).XLabel.String = '$\Delta \mathcal{T}_{ice,0.6/1.2}=\mathcal{T}_{ice,0.6}-\mathcal{T}_{ice,1.2}$ (K)';
ax(p).XLabel.FontSize = fontsize;
ax(p).XLabel.Interpreter = interpreter;
ax(p).YLabel.String = 'Ice Shell Thickness, $D$ (km)';
ax(p).YLabel.FontSize = fontsize;
ax(p).YLabel.Interpreter = interpreter;
ax(p).XLim = [0 50];
ax(p).Layer = 'Top';

%% Warm Convective Layer
